class CaptchaResolverV2
  def self.resolve
    input = File.read("captcha_input.txt")
    numbers = input.to_s.split('').map(&:to_i)
    index = 0
    total_sum = 0
    
    numbers.each do |number|
      if numbers[index + 1].nil?
        total_sum += number if number == numbers[0]
      else
        total_sum += number if number == numbers[index + 1]
      end

      index += 1
    end

    total_sum
  end
end

